prompt Creating function F_VARCHAR2_TO_DATE
prompt ====================================
prompt
CREATE OR REPLACE FUNCTION F_VARCHAR2_TO_DATE(I_FIRST  VARCHAR2,
                                              I_SECOND VARCHAR2)
  RETURN DATE AS
  V_FIRST  VARCHAR2(14) := '';
  V_SECOND VARCHAR2(8) := '';
  V_LEN    NUMBER := LENGTH(I_FIRST);
  V_RESULT DATE := NULL;
BEGIN
--判断第一日期是否存在
  IF I_FIRST IS NOT NULL THEN
    V_FIRST  := SUBSTR(I_FIRST, 1, 14);
    V_SECOND := SUBSTR(I_SECOND, 1, 8);
    BEGIN
      IF V_LEN >= 8 AND V_LEN <= 14 THEN
        V_RESULT := TO_DATE(V_FIRST, 'yyyymmddhh24miss');
      END IF;
    EXCEPTION
      WHEN OTHERS THEN
        IF LENGTH(V_SECOND) = 8 THEN
          V_RESULT := TO_DATE(V_SECOND, 'yyyymmdd');
        END IF;
    END;
  END IF;
  RETURN V_RESULT;
END;
/

