prompt Creating package body PKG_CHECK_REFUND
prompt ======================================
prompt
create or replace package body pkg_check_refund is

  procedure proc_refund_amt_check(i_order_no              t_busi_refund_detail.mchnt_merger_order_no%type,
                                  i_old_mchnt_req_no      t_busi_refund_detail.old_mchnt_req_no%type,
                                  i_old_mchnt_order_no    t_busi_refund_detail.old_mchnt_order_no%type,
                                  i_bank_code             t_org_refund_detail.bank_code%type,
                                   i_merchant_id           t_busi_refund_detail.mchnt_code%type,
                                  i_req_ip                t_org_refund_detail.applicant_ip%type,
                                  i_refund_amt            t_org_refund_detail.refund_amt%type,
                                  i_total_amt             t_org_refund_detail.total_amt%type,
                                  i_req_time              varchar2,
                                  o_return_flag           out varchar2,
                                  o_busi_refund_detail_id out varchar2,
                                  o_org_refund_detail_id  out varchar2) is
    t_refund_total_amt number;--退款总金额
  begin
    t_refund_total_amt := 0;
    begin
       /** 查询被退款商户订单流水在业务退款申请表里的退款总金额 */
      select total_refund_amount
        into t_refund_total_amt
        from (select total_refund_amount
                from t_busi_refund_detail t
               where old_mchnt_req_no = i_old_mchnt_req_no
                     and old_mchnt_order_no = i_old_mchnt_order_no
                 and (refund_status = '1' or refund_status = '2' or
                     refund_status = '6' or refund_status = '7')
               order by t.last_upd_time desc)
       where rownum = 1;
    exception
      /** 没有退款记录 */
      when no_data_found then
        null;
    end;
    t_refund_total_amt := t_refund_total_amt + i_refund_amt;
    if t_refund_total_amt > i_total_amt then
      /* 1119 退款总金额大于订单总金额 */
      o_return_flag := '1119';
      return;
    end if;
    /**  */
    select to_char(sysdate,'yyyymmddhh24mmss')||to_char(SEQ_COMMON_8.nextval) into o_busi_refund_detail_id from dual;
    select to_char(sysdate,'yyyymmddhh24mmss')||to_char(SEQ_COMMON_8.nextval) into o_org_refund_detail_id from dual;
    insert into t_busi_refund_detail
      (id,
       mchnt_code,
       mchnt_merger_order_no,
       mchnt_req_no,
       mchnt_order_no,
       order_date,
       total_amt,--订单总金额
       refund_amt,--此订单退款金额
       total_refund_amount,--订单累计退款总金额
       refund_status, --退款订单状态
       refund_applicant,
       order_adj_status,
       applicant_ip,
       refund_date,
       trans_info_type,
       old_mchnt_req_no,--原订单商户请求流水号
       old_mchnt_order_no,--原订单商户请求定订单号
       create_time,
       last_upd_time)
    values
      (o_busi_refund_detail_id,
       i_merchant_id,
       i_order_no,
       i_order_no,
       i_order_no,
       to_date(substr(i_req_time, 1, 8), 'yyyy-mm-dd'),
       i_total_amt,
       i_refund_amt,
       t_refund_total_amt,
       '3',--订单状态
       i_merchant_id,
       '01',--调单状态
       i_req_ip,
       to_date(i_req_time, 'yyyy-mm-dd hh24:mi:ss'),
       '01',--交易信息类型
       i_old_mchnt_req_no,
       i_old_mchnt_order_no,
       sysdate,
       sysdate
       );

    /** 机构申请退款表 */
    insert into t_org_refund_detail
      (id,
       mchnt_merger_order_no,
       bank_code,
       mchnt_req_no,
       mchnt_order_no,
       refund_amt，
       order_date,
       total_amt,
       order_adj_status，
       refund_status，
       refund_applicant,
       applicant_ip,
       trans_info_type,
       create_time,
       last_upd_time)
    values
      (o_org_refund_detail_id,
       i_order_no,
       i_bank_code,
       i_order_no,
       i_order_no,
       i_refund_amt,
       to_date(substr(i_req_time, 1, 8), 'yyyy-mm-dd'),
       i_total_amt,
       '01', --调单类型
       '3',--订单状态
       i_merchant_id,--退款申请人
       i_req_ip,
       '01', --交易信息类型
       sysdate,
       sysdate
       );
    /* 成功 */
    o_return_flag := '0000';
    commit;
  exception
    when others then
      rollback;
      o_return_flag := '9999';
      return;
  end proc_refund_amt_check;

  procedure proc_back_server_refund(i_our_trans_no   t_order_busi_detail.our_trans_no%type,
                                  i_mchnt_code           t_busi_refund_detail.mchnt_code%type,
                                  i_mchnt_req_no         t_busi_refund_detail.mchnt_req_no%type,
                                  i_mchnt_order_no       t_busi_refund_detail.mchnt_order_no%type,
                                  o_return_flag          out number,
                                  o_rtn_result_msg       out varchar2) is
    t_refund_total_amt number; --退款总金额
    t_trans_amt number;        --交易金额
    t_refund_flag varchar2(1); --退款标识:0:未退款1:已退款2:部分退款3:已冲正
    t_update_count number;     --修改订单标识成功记录数
    begin
      begin
         t_refund_total_amt := 0;
         t_trans_amt := 0;
        --查询业务退款申请表 获取订单退款累计总金额  和原始订单总金额
       select total_refund_amount,total_amt into t_refund_total_amt,t_trans_amt
                from t_busi_refund_detail t
               where mchnt_req_no = i_mchnt_req_no and mchnt_order_no = i_mchnt_order_no
                     and mchnt_code = i_mchnt_code
                 and  refund_status = '6';
      exception
        when no_data_found then
          null;
      end;
      if t_refund_total_amt > t_trans_amt then
        o_return_flag := -1;
        o_rtn_result_msg := '退款总金额:'||t_refund_total_amt||'大于 原始订单交易金额:'||t_trans_amt;
        return;
      elsif t_refund_total_amt = t_trans_amt then
        t_refund_flag := '1'; --1:(全额退款)已退款
      else
        t_refund_flag := '2'; --2:部分退款
      end if;

      --修改订单业务信息表退款标识
       update t_order_busi_detail set refund_flag = t_refund_flag
              where our_trans_no = i_our_trans_no ;
       t_update_count := sql%rowcount;
       if t_update_count = 0 then
         rollback;
         o_return_flag := -1;
         o_rtn_result_msg := '订单业务信息表退款标识修改失败';
         return ;
       end if;

      --修改修改业务流水表退款标识
      update t_busi_trans_detail set refund_flag   = t_refund_flag
              where our_trans_no = i_our_trans_no ;
       t_update_count := sql%rowcount;
        if t_update_count = 0 then
         rollback;
          o_return_flag := -1;
          o_rtn_result_msg := '业务流水退款标识修改失败';
         return ;
       end if;

       --业务退款申请表
       update t_busi_refund_detail set refund_status   = '7'
              where  mchnt_req_no = i_mchnt_req_no and mchnt_order_no = i_mchnt_order_no
                     and mchnt_code = i_mchnt_code
                 and  refund_status = '6';
       t_update_count := sql%rowcount;
        if t_update_count = 0 then
         rollback;
          o_return_flag := -1;
          o_rtn_result_msg := '业务退款申请表退款状态修改失败';
         return ;
       end if;

      o_return_flag := 1;
      commit;

      exception
    when others then
      rollback;
      o_return_flag := -1;
      o_rtn_result_msg := '处理异常';
      update t_busi_refund_detail set refund_status = '8'
             where mchnt_req_no = i_mchnt_req_no and mchnt_order_no = i_mchnt_order_no
                     and mchnt_code = i_mchnt_code
                 and  refund_status = '6';
      return;
    end proc_back_server_refund;

end pkg_check_refund;
/

