  function get_enddate(i_date_bgn varchar2) return varchar2 is
    l_tmp_date date;
  begin
    if length(i_date_bgn) = 6 then
      l_tmp_date := to_date(i_date_bgn, 'yyyymm');
      return to_char(add_months(l_tmp_date, 1), 'yyyymm');
    elsif length(i_date_bgn) = 8 then
      l_tmp_date := to_date(i_date_bgn, 'yyyymmdd');
      return to_char(l_tmp_date + 1, 'yyyymmdd');
    else
      raise_application_error(-20000, '-');
    end if;
  exception
    when others then
      raise_application_error(-20000,
