        from up_comm_tran_detail td, up_comm_tran_attach ta
       where td.up_tran_seq = ta.up_tran_seq(+)
         and td.up_tran_seq >= i_date_bgn
         and td.up_tran_seq < l_date_end log errors reject limit
       unlimited;
