  /*
    name: proc_auto_part
    desc: 基于时间的分区表自动维护,按天分区,默认添加到下个月底,通过scheduler job定时调用
           能够被维护的表需满足以下的要求:
            1.分区表必须是range分区,非复合分区
            2.分区表命名必须是字符'P' + 年月日9位字符组成,例:P20130117
            3.如果数据库是11g以上,则可以考虑使用Interval Partitioning功能
    input args: none
    output args: none
    author: guqin
    created: 2013-1-17
  */
  procedure proc_auto_part is
    procedure add_part_sub(i_tabname varchar2) is
      l_curr_part_date date;
      l_tabname        varchar2(30);
      l_tbsname        varchar2(30);
      l_cnt            pls_integer;
      l_partname       varchar2(10);
      l_bound_date     varchar2(10);
    begin
      l_tabname := upper(i_tabname);
      select curr_part_date, tablespace_name
        into l_curr_part_date, l_tbsname
        from (select to_date(substr(partition_name, 2), 'yyyymmdd') curr_part_date,
                     tablespace_name,
                     row_number() over(order by partition_name desc) rn
                from user_tab_partitions t
               where t.table_name = l_tabname)
       where rn = 1;
      /*判断分区最大月是否已添加到下月底,进行相应维护*/
      l_cnt := add_months(last_day(trunc(sysdate)), 1) - l_curr_part_date;
      if l_cnt > 0 then
        for i in 1 .. l_cnt loop
          l_partname   := 'P' || to_char(l_curr_part_date + i, 'yyyymmdd');
          l_bound_date := to_char(l_curr_part_date + i + 1, 'yyyy-mm-dd');
          execute immediate 'alter table ' || l_tabname ||
                            ' add partition ' || l_partname ||
                            ' values less than ( date''' || l_bound_date ||
                            ''') tablespace ' || l_tbsname;
        end loop;
      end if;
    exception
      when others then
        dbms_output.put_line(l_tabname ||
                             'is not a valid partition table,ignore!');
    end;
  begin
    for it in (select table_name
                 from user_part_tables
                where table_name in ('T_BUSI_SETTLE_DETAIL',
                                     'T_PAY_SETTLE_DETAIL',
                                     'T_BUSI_SPLIT_DETAIL',
                                     'T_BUSI_TRANS_DETAIL',
                                     'T_PAY_TRANS_DETAIL',
                                     'T_ORDER_MAIN',
                                     'T_ORDER_BUSI_DETAIL',
                                     'T_ORDER_PAY_DETAIL',
                                     'T_ORDER_BUSI_FIELD_DETAIL',
                                     'T_SHARE_DETAIL',
                                     'T_CONN_LOG'
                                     /*'T_TRANS_LOG'*/)) loop
      add_part_sub(it.table_name);
    end loop;
    -- raise exception to outer
  end proc_auto_part;

  /*
    name: proc_auto_dict
    desc:
    input args: none
    output args: none
    author: guqin
    created: 2013-3-25
  */
  procedure proc_auto_dict is
    l_item_name   t_data_dict.item_name%type;
    l_col_name_cn t_data_dict.col_name_cn%type;
    l_line        varchar2(500);
    l_col_name    t_data_dict.col_name%type;
    l_value       t_data_dict.item_val%type;
    l_cnt         pls_integer;
    procedure p_split_sub(i_str   varchar2,
                          o_item  out nocopy varchar2,
                          o_value out nocopy varchar2) is
    begin
      o_item  := substr(i_str, 1, instr(i_str, ':') - 1);
      o_value := substr(i_str, instr(i_str, ':') + 1);
    end;
  begin
    for i in (select * from user_col_comments where comments like 'dict:%') loop
      l_item_name := i.table_name;
      l_cnt       := 1;
      loop
        l_line := regexp_substr(i.comments, '^.*$', 1, l_cnt, 'm');
        exit when l_line is null;
        p_split_sub(l_line, l_col_name, l_value);
        if lower(l_col_name) = 'dict' then
          if lower(l_value) = 'comm' then
            l_item_name := upper('common_value');
          end if;
          goto sub_continue; --11g continue;
        end if;
        if l_value is null then
          l_col_name_cn := l_col_name;
          goto sub_continue;
        end if;
        begin
          insert into t_data_dict
            (id, item_name, col_name_cn, col_name, item_val, item_desc)
          values
            (seq_common_8.nextval,
             l_item_name,
             l_col_name_cn,
             i.column_name,
             l_col_name,
             l_value);
        exception
          when dup_val_on_index then
            null;
          when others then
            dbms_output.put_line(sqlerrm);
            dbms_output.put_line('table=' || i.table_name || ',col=' ||
                                 i.column_name || ',comment=' ||
                                 i.comments);
            raise;
        end;
        <<sub_continue>>
        l_cnt := l_cnt + 1;
      end loop;
    end loop;
    commit;
  exception
    when others then
      rollback;
      raise;
  end;

