         i_our_trans_no,
         i_mchnt_code,
         i_mchnt_order_no,
         i_mchnt_req_no,
         i_acq_org_code,
         i_order_date,
         i_settle_date,
         'A', --settle_status
         null, --settle_batch_no
         i_busi_channel,
         i_conn_channel,
         i_trans_code,
         i_total_amt, --trans_amt
         i_trans_status, --trans_status
         i_commission_amt,
         'C', --chk_status
         sysdate,
         i_busi_req_no,
         i_reverse_flag,
         i_split_flag, --split_flag 分账标识0分账交易1非分账交易
         null, --split_batch_no
         'A', --split_status A未分账
         i_refund_flag,
         '0', --delete_flag,
         i_accept_srv_ip,
         i_mchnt_merger_order_req_no,
         i_mchnt_merger_order_date,
         i_mchnt_order_no,
         i_settle_flag);
    exception
      when dup_val_on_index then
        o_returnno := -4;
