    when others then
      --o_returnno  := -1;
      o_returnmsg := 'exception' || sqlerrm;
  end;

  /*
    name: proc_order_mian_permanent
    desc: 订单主表订单号重复性校验及持久化
    input args:
      具体请看   t_order_main
                 表的字段含义
  */
  procedure proc_order_mian_permanent(i_mchnt_merger_order_no     t_order_main.mchnt_merger_order_no%type,
                                      i_conn_channel              t_order_main.conn_channel%type,
                                      i_mchnt_merger_order_req_no t_order_main.mchnt_merger_order_req_no%type,
                                      i_mchnt_merger_order_date   t_order_main.mchnt_merger_order_date%type,
                                      i_total_amt                 t_order_main.total_amt%type,
                                      i_pg_url                    t_order_main.pg_url%type,
                                      i_bg_url                    t_order_main.bg_url%type,
                                      i_client_ip                 t_order_main.client_ip%type,
                                      i_mac                       t_order_main.mac%type,
                                      i_our_trans_no              t_order_main.our_trans_no%type,
                                      i_old_our_trans_no          t_order_main.old_our_trans_no%type,
                                      i_settle_date               t_order_main.settle_date%type,
                                      i_initiator_code            t_order_main.initiator_code%type,
                                      i_callback_type             t_order_main.callback_type%type,
                                      i_order_status              t_order_main.order_status%type,
                                      i_notify_status             t_order_main.notify_status%type,
                                      i_notify_type               t_order_main.notify_type%type,
                                      i_delete_flag               t_order_main.delete_flag%type,
                                      i_accept_srv_ip             t_order_main.accept_srv_ip%type,
                                      i_payable_amt               t_order_main.payable_amt%type,
                                      i_real_amt                  t_order_main.real_amt%type,
                                      i_coupon_amt                t_order_main.coupon_amt%type,
                                      i_chk_status                t_order_main.chk_status%type,
                                      i_reverse_flag              t_order_main.reverse_flag%type,
                                      i_curtype                   t_order_main.curtype%type,
                                      i_encrypt_type              t_order_main.encrypt_type%type,
                                      i_attach                    t_order_main.attach%type,
                                      o_returnno                  out number,
                                      o_returnmsg                 out varchar) is
    l_cur_datetime t_order_main.create_time%type := sysdate; --当前时间
    l_row_num      number; --主订单号及请求流水号是否存在
  begin
    --校验主订单号或者请求流水号重复
    select count(1)
      into l_row_num
      from t_order_main
     where mchnt_merger_order_no = i_mchnt_merger_order_no
       --and mchnt_merger_order_req_no = i_mchnt_merger_order_req_no
       and mchnt_merger_order_date < trunc(i_mchnt_merger_order_date) + 1
       and mchnt_merger_order_date >= trunc(i_mchnt_merger_order_date)
       and order_status in ('A','B')
       and initiator_code = i_initiator_code;
    if l_row_num >= 1 then
      o_returnno  := 99;
      o_returnmsg := '外部商户总订单号重复';
      return;
    end if;
    --校验请求流水号重复
    select count(1)
      into l_row_num
      from t_order_main
     where mchnt_merger_order_req_no = i_mchnt_merger_order_req_no;
    if l_row_num >= 1 then
      o_returnno  := 98;
      o_returnmsg := '请求流水号重复';
      return;
    end if;
    insert into t_order_main
      (id,
       mchnt_merger_order_no,
       conn_channel,
       mchnt_merger_order_req_no,
       mchnt_merger_order_date,
       total_amt,
       pg_url,
       bg_url,
       client_ip,
       mac,
       our_trans_no,
       old_our_trans_no,
       settle_date,
       initiator_code,
       callback_type,
       order_status,
       notify_status,
       notify_type,
       delete_flag,
       create_time,
       last_upd_time,
       accept_srv_ip,
       payable_amt,
       real_amt,
       coupon_amt,
       chk_status,
       reverse_flag,
       curtype,
       encrypt_type,
       attach)
    values
      (f_seq_order_main,
       i_mchnt_merger_order_no,
       i_conn_channel,
       i_mchnt_merger_order_req_no,
       i_mchnt_merger_order_date,
       i_total_amt,
       i_pg_url,
       i_bg_url,
       i_client_ip,
       i_mac,
       i_our_trans_no,
       i_old_our_trans_no,
       i_settle_date,
       i_initiator_code,
       i_callback_type,
       i_order_status,
       i_notify_status,
       i_notify_type,
       i_delete_flag,
       l_cur_datetime,
       l_cur_datetime,
       i_accept_srv_ip,
       i_payable_amt,
       i_real_amt,
       i_coupon_amt,
       i_chk_status,
       i_reverse_flag,
       i_curtype,
       i_encrypt_type,
       i_attach);

    o_returnno  := 1;
    o_returnmsg := 'success';
  exception
    when others then
      o_returnno  := -1;
      o_returnmsg := 'exception' || sqlerrm;
  end;

  /*
    name: upd_resp_pay_trans
    desc: 通知消息回写
    input args:
      具体请看   t_order_main
                 表的字段含义

    --1001 支付流水表无此记录
    --1002 支付流水表判断交易金额不一致
    --1003 支付流水表请求通知重复响应
    --9999 系统错误
  */
  procedure upd_resp_pay_trans(i_pay_req_id         t_pay_trans_detail.pay_req_no%type,
                               i_pay_resp_date      t_pay_trans_detail.pay_resp_date%type,
                               i_pay_resp_id        t_pay_trans_detail.pay_resp_no%type,
                               i_pay_resp_code      t_pay_trans_detail.pay_resp_code%type,
                               i_pay_resp_desc      t_pay_trans_detail.pay_resp_desc%type,
                               i_trans_amt          t_pay_trans_detail.trans_amt%type,
                               i_trans_status       t_pay_trans_detail.trans_status%type,
                               i_authorization_code t_pay_trans_detail.authorization_code%type,
                               i_pay_org_code       t_pay_trans_detail.pay_org_code%type,
                               i_resp_srv_ip        t_pay_trans_detail.resp_srv_ip%type,
                               o_returncode         out varchar2) is
    l_trans_amt_tmp    t_pay_trans_detail.trans_amt%type;
    l_trans_status_tmp t_pay_trans_detail.trans_status%type;
  begin
    o_returncode := '9999';
    begin
      select trans_amt, trans_status
        into l_trans_amt_tmp, l_trans_status_tmp
        from t_pay_trans_detail
       where pay_req_no = i_pay_req_id
         and pay_org_code = i_pay_org_code
         for update;
    exception
      when no_data_found then
        o_returncode := '1001';
        return;
    end;
    /*判断交易金额，注意：如果i_trans_amt is null 则不加以判断*/
    if i_trans_amt != l_trans_amt_tmp then
      o_returncode := '1002';
      return;
    end if;
    /*判断是否重复通知*/
    if l_trans_status_tmp != 'A' then
      o_returncode := '1003';
      return;
    end if;
    /*更新记录*/
    update t_pay_trans_detail
       set trans_status       = i_trans_status,
           pay_resp_date      = nvl(i_pay_resp_date,sysdate),
           pay_resp_no        = i_pay_resp_id,
           pay_resp_code      = i_pay_resp_code,
           pay_resp_desc      = i_pay_resp_desc,
           authorization_code = i_authorization_code,
           resp_srv_ip        = i_resp_srv_ip
     where pay_req_no = i_pay_req_id
       and pay_org_code = i_pay_org_code;
    /*正确返回*/
    o_returncode := '0000';
    commit;
  exception
    when others then
      rollback;
      o_returncode := sqlerrm;
  end;

  /*
    name: proc_conn_log
    desc: 接入日志记录，成功返回平台流水号
    input args:
      具体请看t_conn_log表的字段含义
    output args: o_returnno 0成功 -1 失败
                 o_main_order_no 平台流水号
    author: guqin
    created: 2013-5-16
  */
  procedure proc_conn_log(i_mchnt_merger_order_no t_conn_log.mchnt_merger_order_no%type,
                          i_msg_content           t_conn_log.msg_content%type,
                          i_client_ip             t_conn_log.client_ip%type,
                          i_accept_srv_ip          t_conn_log.accept_srv_ip%type,
                          o_returnno              out number,
                          o_our_trans_no          out t_conn_log.our_trans_no%type,
                          o_settle_date           out t_daycut_ctrl.settle_date%type) is
    l_seq_tmp pls_integer;
  begin
    /*获取清算日期*/
    select max(settle_date)
      into o_settle_date
      from t_daycut_ctrl
     where dcut_status = '02';
    /*生成交易流水号*/
    select seq_our_trans_no_14.nextval into l_seq_tmp from dual;
    o_our_trans_no := to_char(o_settle_date, 'YYYYMMDD') ||
                      lpad(to_char(l_seq_tmp), 14, '0');
    insert into t_conn_log
      (id,
       settle_date,
       our_trans_no,
       mchnt_merger_order_no,
       msg_content,
       client_ip,
       accept_srv_ip
      )
    values
      (to_char(sysdate, 'yyyymmddhh24miss') ||
       lpad(to_char(seq_conn_log_8.nextval), 8, '0'),
       o_settle_date,
       o_our_trans_no,
       i_mchnt_merger_order_no,
       i_msg_content,
       i_client_ip,
       i_accept_srv_ip
      );
    o_returnno := 0;
    commit;
  exception
    when others then
      o_returnno     := -1;
      o_our_trans_no := null;
      rollback;
  end;
end pkg_trans;
